mapper.select:
select

from ht_inter_tb

	OP_TYPE!='D'

		and INTER_ID = #{interId,jdbcType=INTEGER}


		and INTER_NAME LIKE
		CONCAT(CONCAT(#{interName,jdbcType=VARCHAR},'%'),'%')


		and REMARK = #{remark,jdbcType=VARCHAR}


		and OP_TYPE = #{opType,jdbcType=CHAR}


		and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


		and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


		and CREATOR_ID = #{creatorId,jdbcType=VARCHAR}


		and MODIFIER_ID = #{modifierId,jdbcType=VARCHAR}


ORDER BY CREATE_TIME DESC

select

from ht_inter_tb

	OP_TYPE!='D' AND INTER_ID = #{interId,jdbcType=INTEGER}
