mapper.insert:
insert into ht_query_dim_detail_tb (QDD_ID, QD_ID, KEY_CODE, 
  VAL, IS_DEFAULT, OP_TYPE, CREATE_TIME, 
  MODIFY_TIME, CREATOR_ID, MODIFIER_ID
  )
values (#{qddId,jdbcType=INTEGER}, #{qdId,jdbcType=INTEGER}, #{keyCode,jdbcType=VARCHAR}, 
  #{val,jdbcType=VARCHAR}, #{isDefault,jdbcType=CHAR}, #{opType,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{creatorId,jdbcType=VARCHAR}, #{modifierId,jdbcType=VARCHAR}
  )

mapper.select:
select

from ht_query_dim_detail_tb
OP_TYPE!='D'

		and QDD_ID = #{qddId,jdbcType=INTEGER}


		and QD_ID = #{qdId,jdbcType=INTEGER}


		and KEY_CODE LIKE CONCAT(CONCAT('%',#{keyCode}),'%')


		and VAL LIKE CONCAT(CONCAT('%',#{val}),'%')


		and IS_DEFAULT = #{isDefault,jdbcType=CHAR}


		and OP_TYPE = #{opType,jdbcType=CHAR}


		and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


		and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


		and CREATOR_ID = #{creatorId,jdbcType=VARCHAR}


		and MODIFIER_ID = #{modifierId,jdbcType=VARCHAR}
