mapper.select:
select

from ht_query_dim_tb

	OP_TYPE!='D'

		and QD_ID = #{qdId,jdbcType=INTEGER}


		and QUERY_DIM_CODE LIKE
		CONCAT(CONCAT(#{queryDimCode,jdbcType=VARCHAR},'%'),'%')


		and QUERY_DIM_NAME LIKE
		CONCAT(CONCAT(#{queryDimName,jdbcType=VARCHAR},'%'),'%')


		and IS_AUTH = #{isAuth,jdbcType=CHAR}


		and QUERY_DIM_TYPE = #{queryDimType,jdbcType=CHAR}


		and QUERY_DIM_SHOW_TYPE =
		#{queryDimShowType,jdbcType=CHAR}


		and OP_TYPE = #{opType,jdbcType=CHAR}


		and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


		and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


		and CREATOR_ID = #{creatorId,jdbcType=VARCHAR}


		and MODIFIER_ID = #{modifierId,jdbcType=VARCHAR}


ORDER BY CREATE_TIME DESC
