mapper.select:
select

from ht_sql_alias_tb

	OP_TYPE!='D'

		and SA_ID = #{saId,jdbcType=INTEGER}


		and TRS_ID = #{trsId,jdbcType=INTEGER}


		and TABLE_NAME = #{tableName,jdbcType=VARCHAR}


		and ALIAS_NAME = #{aliasName,jdbcType=VARCHAR}


		and OP_TYPE = #{opType,jdbcType=CHAR}


		and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


		and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


		and CREATOR_ID = #{creatorId,jdbcType=VARCHAR}


		and MODIFIER_ID = #{modifierId,jdbcType=VARCHAR}
