mapper.select:
select

from ht_tar_reg_sql_tb

	op_type != 'D'

		AND TRS_ID=#{trsId}


		AND REG_ID=#{regId}


		AND TARGET_CODE like '%${targetCode}%'


		AND SUB_CODE=#{subCode}


		AND GROUP_CODE=#{groupCode}


		AND GROUP_DETAIL_CODE=#{groupDetailCode}


		AND DATE_TYPE=#{dateType}

	AND OP_TYPE!='D'

SELECT
TRS_ID,
REG_ID,
TARGET_CODE,
SUB_CODE,
GROUP_CODE,
GROUP_DETAIL_CODE,
DATE_TYPE,
SQL_CODE
FROM
HT_TAR_REG_SQL_TB
WHERE
TARGET_CODE =#{target} AND
REG_ID=#{regId}


		AND GROUP_CODE=#{groupType}


				AND DATE_TYPE=#{dateType}




				AND GROUP_DETAIL_CODE=#{groupDetail}





			AND DATE_TYPE=#{dateType}


			AND GROUP_DETAIL_CODE=#{groupDetail}



AND OP_TYPE!='D'

SELECT TRS_ID, REG_ID, TARGET_CODE, SUB_CODE, GROUP_CODE, GROUP_DETAIL_CODE, DATE_TYPE, SQL_CODE FROM HT_TAR_REG_SQL_TB WHERE TARGET_CODE =#{target} AND REG_ID=#{regId}
AND GROUP_TYPE=#{groupType}
AND (GROUP_TYPE IS NULL OR GROUP_TYPE='')
AND GROUP_DETAIL_CODE=#{groupDetail}
AND (GROUP_DETAIL_CODE IS NULL OR GROUP_DETAIL_CODE='')
AND DATE_TYPE=#{dateType}
AND (DATE_TYPE IS NULL OR DATE_TYPE='')
AND OP_TYPE!='D'
