mapper.insert:
insert into ht_warn_result_tb (WR_ID, BS_ID, USERNAME, 
  ROLE_CODE, WARN_VAL, FIRST_WARN_TIME, 
  WARN_TIME, IS_READ, READ_TIME
  )
values (#{wrId,jdbcType=INTEGER}, #{bsId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, 
  #{roleCode,jdbcType=VARCHAR}, #{warnVal,jdbcType=REAL}, #{firstWarnTime,jdbcType=TIMESTAMP}, 
  #{warnTime,jdbcType=TIMESTAMP}, #{isRead,jdbcType=CHAR}, #{readTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from ht_warn_result_tb


		and ROLE_CODE = #{roleCode}


		and USERNAME = #{username}


order by warn_time desc

select w.WR_ID, w.BS_ID, w.USERNAME, w.ROLE_CODE, w.WARN_VAL, w.FIRST_WARN_TIME, w.WARN_TIME, w.IS_READ, w.READ_TIME from ht_warn_result_tb w LEFT JOIN ht_user_set_warn_tb u ON u.BS_ID=w.BS_ID LEFT JOIN ht_target_tb t ON u.TARGET_CODE =t.TARGET_CODE 1=1
and w.ROLE_CODE = #{roleCode}
and w.USERNAME = #{username}
and t.TARGET_NAME LIKE CONCAT(CONCAT('%',#{targetName}),'%')
ORDER BY w.read_time desc
